#include <math.h>

//==============================================================================
//  DEFINES
//==============================================================================

#undef MIN
#undef MAX

#define SQR( a )                    ( (a) * (a) )
#define ABS( a )                    ( ((a) >  0 ) ? (a) : -(a) )
#define MIN( a, b )                 ( ((a) < (b)) ? (a) :  (b) )
#define MAX( a, b )                 ( ((a) > (b)) ? (a) :  (b) )
#define MINMAX( Min, Val, Max )     ( MAX( (Min), MIN((Val),(Max)) ) )
#define IN_RANGE( Min, Val, Max )   ( ((Min)<=(Val)) && ((Val)<=(Max)) )

typedef float radian;

//==============================================================================
//  sincosf
//==============================================================================

inline 
void sincosf( radian Angle, float& S, float& C )
{
    S = sinf( Angle );
    C = cosf( Angle );
}

//==============================================================================
//  random
//==============================================================================

inline
float frand( float Min, float Max )
{
    return Min + ((rand() * (Max-Min)) / RAND_MAX);
}

inline
int irand( int Min, int Max )
{
    return Min + (rand() % (Max-Min+1));
}

//==============================================================================
//  Macros for angle conversion and radian angular constants.
//==============================================================================
//
//  RADIAN( degrees )       - Converts degrees to radians.
//  DEG_TO_RAD( degrees )   - Converts degrees to radians.
//  RAD_TO_DEG( radians )   - Converts radians to degrees.
//
//  R_0, R_1, ... R_360     - Angles in radians of all integral degree angles
//                            from 0 to 360.
//
//  As an aside:  Note that "R_0" means "no rotation" whereas "0.0f" just means
//  "zero".  What's the difference?  None to the compiler.  But "R_0" gives more
//  information to anybody reading the code.  Think of it as "strong typing".
//
//------------------------------------------------------------------------------

#define RADIAN(A)     ((radian)((A) *  0.017453292519943295769236905556f))
#define DEG_TO_RAD(A) ((radian)((A) *  0.017453292519943295769236905556f))
#define RAD_TO_DEG(A) (( float)((A) * 57.295779513082320876798161804285f))

